"use strict";
var Http = {

	sendRequest: function(url, params, callbackFunction, httpMethod){

		if(null == httpMethod){
			httpMethod = 'GET';
		}
		var ajaxAttributes = {
			type: httpMethod,
			url: url,
			data: params,
			beforeSend: function(){
			}
		};
		$.ajax(ajaxAttributes).
		done(function(data){
			callbackFunction(data);
		}).
		fail(function(){
			alert('fail');
		}).
		always(function(){
		});
	},
	
	/**
	 * @param orderbyList: key/value list: key=fieldId,val=fieldOrder
	*/
	buildOrderBy(orderbyList){
	
		var result = new Array();
		//for(var i=0; i<orderbyList; i++){
		for(var i in orderbyList){
			result.push(i + '_' + orderbyList[i]);
		}
		return result.join('##');
	},
	
	copyModel: function(elemToCopy, elemTarget, callbackFunction){
		
		var copiedElem = elemToCopy.clone();
		copiedElem.removeClass('cubee-model');
		copiedElem.find('input, select').prop('disabled', false);
		elemTarget.append(copiedElem);
		callbackFunction(copiedElem);
	}
};
var StdTableFilter = function(options){

	var me = this;	
	
	/////////// global div for filters /////////////
	this.createGlobalDiv = function(){
	
		me.globalDiv = $('<div class="cubee-table-filter"><div>'
				+	'	<div class="cubee-table-filter-title">Filtres </div>'
				+	'	<div class="cubee-model">'
				+	'		<select disabled="disabled" name="filter-operator"><option value="0">=</option><option value="1">%</option><option value="2"><=</option><option value="3">>=</option><option value="4">!=</option></select> <input disabled="disabled" name="filter-value" type="text" placeholder=" valeur" />'
				+	'	</div>'
				+ 	'</div></div>');
	}
	
	////////////////////////////////////////////////////////////////////////////////////////////////
	////////////// constructor body ///////////////////////////////////////////////////////////////
	//////////////////////////////////////////////////////////////////////////////////////////////
	this.constructor = function(){
	
		/////	span filter		/////
		var spanAddFilter = $('<span class="cubee-action">+</span>');
		spanAddFilter.on('click', function(){
			var thisElem = $(this);
			if(null == me.spanAddFilter.nbFilterElem){
				me.spanAddFilter.nbFilterElem = 0;
			}
			if(0 == me.spanAddFilter.nbFilterElem){
				me.boutonSendFilter.show();
			}
			me.spanAddFilter.nbFilterElem = me.spanAddFilter.nbFilterElem + 1;
			var parent = thisElem.parent().parent();
			var divModel = parent.children('div.cubee-model');
			Http.copyModel(divModel, parent, function(newDiv){
				var spanDeleteLine = $('<span class="cubee-action"> X </span>');
				spanDeleteLine.on('click', function(){
					newDiv.remove();
					me.spanAddFilter.nbFilterElem = me.spanAddFilter.nbFilterElem - 1;
					if(0 == me.spanAddFilter.nbFilterElem){
						me.boutonSendFilter.hide();
					}
				});
				newDiv.append(spanDeleteLine);
			});
		});
		this.spanAddFilter = spanAddFilter;
		
		/////	<select> for filter field	/////
		var selectField = $('<select disabled="disabled" name="filter-field-id"><option>** choix **</option></select>');
		for(var i=0; i<options.columnList.length; i++){
			var column = options.columnList[i];
			selectField.append($('<option value="'+ column.fieldId +'">'+ column.label +'</option>'));
		}
		me.selectField = selectField;
		
		/////	bouton post filter	/////
		var boutonSendFilter = $('<input type="submit" />');
		boutonSendFilter.hide();
		me.boutonSendFilter = boutonSendFilter;
		me.createGlobalDiv();
		
		me.globalDiv.find('.cubee-table-filter-title').append(me.spanAddFilter);
		me.globalDiv.find('.cubee-model').prepend(me.selectField);
		me.globalDiv.append(me.boutonSendFilter);
	}
	
	this.constructor();
	
	
};
var StdTablePaging = function(stdTable, options){

	var me = this;	
	
	/////////// global div for filters /////////////
	this.createGlobalDiv = function(){
	
		me.globalDiv = $('<div class="cubee-table-filter">'
				+ 	'<div class="t07-center">'
				+ 		'<div>'
				+			'<a href="#" class="rew-total"> << </a>'
				+			'<a href="#" class="rew"> < </a>'
				+			'Page <input type="text" class="nbpage" value="1"> / <span class="nbpage">1</span>'
				+			'<a href="#" class="fwd"> > </a>'
				+			'<a href="#" class="fwd-total"> >> </a>'
				+		'</div>'
				+	'</div>'
				+'</div>');
	},
	
	this.updatePageNumber = function(pageNumber){
	
		me.globalDiv.find('.nbpage').val(pageNumber);
	};
	
	////////////////////////////////////////////////////////////////////////////////////////////////
	////////////// constructor body ///////////////////////////////////////////////////////////////
	//////////////////////////////////////////////////////////////////////////////////////////////
	this.constructor = function(){
	
		me.createGlobalDiv();
		me.currentPage = 1;
		
		me.globalDiv.find('a').click(function(event){
			event.preventDefault();
			var hrefElem = $(this);
			if(hrefElem.hasClass('rew-total')){
				me.currentPage = 1;
			}
			else if(hrefElem.hasClass('rew')){
				me.currentPage = Math.max(me.currentPage - 1, 1);
			}
			else if(hrefElem.hasClass('fwd')){
				me.currentPage = me.currentPage + 1;
			}
			/*var params = {
				'page-num': me.currentPage
			};
			Http.sendRequest('http://toto.com', params, function(response){
				thisTable.append('<tr></tr>');
			});*/
			me.updatePageNumber(me.currentPage);
			stdTable.triggerPageChange(me.currentPage);
		});
	}
	
	this.constructor();
	
};
